import React from 'react';

import Modal from 'react-modal';



interface PlayerModalProps {

  player: string | null;

  onClose: () => void;

  onRecordAction: (actionType: string) => void;

}



const PlayerModal: React.FC<PlayerModalProps> = ({ player, onClose, onRecordAction }) => {

  if (!player) return null;



  const playerActions = [

    { name: 'Goal', key: 'goal' },

    { name: 'Assist', key: 'assist' },

    { name: 'Foul', key: 'foul' },

  ];



  return (

    <Modal isOpen={!!player} onRequestClose={onClose} contentLabel="Player Actions">

      <h2>Actions for {player}</h2>

      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>

        {playerActions.map((action) => (

          <button

            key={action.key}

            onClick={() => onRecordAction(action.key)}

            style={{

              padding: '10px 20px',

              backgroundColor: '#007bff',

              color: '#fff',

              border: 'none',

              borderRadius: '5px',

              cursor: 'pointer',

            }}

          >

            {action.name}

          </button>

        ))}

      </div>

      <button onClick={onClose} style={{ marginTop: '20px' }}>Close</button>

    </Modal>

  );

};



export default PlayerModal;
