import React from 'react';

import Modal from 'react-modal';



interface PlayerModalProps {

  player: string | null;

  onClose: () => void;

}



const PlayerModal: React.FC<PlayerModalProps> = ({ player, onClose }) => {

  if (!player) return null;



  return (

    <Modal isOpen={!!player} onRequestClose={onClose} contentLabel="Player Actions">

      <h2>Actions for {player}</h2>

      <form>

        <label>

          Goals:

          <input type="number" name="goals" min="0" />

        </label>

        <label>

          Assists:

          <input type="number" name="assists" min="0" />

        </label>

        <label>

          Minutes Played:

          <input type="number" name="minutes" min="0" max="90" />

        </label>

        <button type="submit">Save</button>

        <button type="button" onClick={onClose}>Cancel</button>

      </form>

    </Modal>

  );

};

export default PlayerModal;
