import React, { useState } from 'react';
import Modal from 'react-modal';

interface PlayerModalProps {
  player: string | null;
  onClose: () => void;
}

const PlayerModal: React.FC<PlayerModalProps> = ({ player, onClose }) => {
  const [actions, setActions] = useState<{ [key: string]: number }>({
    goal: 0,
    assist: 0,
    foul: 0,
  });

  if (!player) return null;

  // Define actions for the player
  const playerActions = [
    { name: 'Goal', key: 'goal' },
    { name: 'Assist', key: 'assist' },
    { name: 'Foul', key: 'foul' },
  ];

  // Function to handle action click
  const handleActionClick = (actionKey: string) => {
    setActions((prevActions) => ({
      ...prevActions,
      [actionKey]: prevActions[actionKey] + 1,
    }));
  };

  return (
    <Modal isOpen={!!player} onRequestClose={onClose} contentLabel="Player Actions">
      <h2>Actions for {player}</h2>

      {/* Display buttons for each action */}
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        {playerActions.map((action) => (
          <button
            key={action.key}
            onClick={() => handleActionClick(action.key)}
            style={{
              padding: '10px 20px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            {action.name}
          </button>
        ))}
      </div>

      {/* Display recorded actions */}
      <div>
        <h3>Recorded Actions:</h3>
        <ul>
          {Object.entries(actions).map(([action, count]) => (
            <li key={action}>
              {action.charAt(0).toUpperCase() + action.slice(1)}: {count}
            </li>
          ))}
        </ul>
      </div>

      <button onClick={onClose} style={{ marginTop: '20px' }}>Close</button>
    </Modal>
  );
};

export default PlayerModal;
