import React from 'react';

interface PlayerModalProps {
  player: string;
  onClose: () => void;
  onRecordAction: (player: string, actionType: 'goal' | 'assist' | 'foul') => void;
}

const PlayerModal: React.FC<PlayerModalProps> = ({ player, onClose, onRecordAction }) => {
  return (
    <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', backgroundColor: '#fff', padding: '20px', border: '1px solid #ccc', borderRadius: '5px' }}>
      <h2>Record Action for {player}</h2>
      <button onClick={() => onRecordAction(player, 'goal')}>Goal</button>
      <button onClick={() => onRecordAction(player, 'assist')}>Assist</button>
      <button onClick={() => onRecordAction(player, 'foul')}>Foul</button>
      <button onClick={onClose} style={{ marginTop: '20px', padding: '10px 20px' }}>Close</button>
    </div>
  );
};

export default PlayerModal;
