import React, { useState, useEffect } from 'react';

import Field from './components/Field';

import PlayerModal from './components/PlayerModal';



export interface PlayerActionCounts {

  goal: number;

  assist: number;

  foul: number;

}

interface PlayerData {
  [playerName: string]: {
    actions: { type: keyof PlayerActionCounts; time: string }[];
    actionCounts: PlayerActionCounts;
  };
}


const App: React.FC = () => {

  const [selectedPlayer, setSelectedPlayer] = useState<string | null>(null);

  // const [playerActions, setPlayerActions] = useState<{ [key: string]: PlayerActionCounts }>({

  //   'Player 1': { goal: 0, assist: 0, foul: 0 },

  //   'Player 2': { goal: 0, assist: 0, foul: 0 },

  //   'Player 3': { goal: 0, assist: 0, foul: 0 },

  //   'Player 4': { goal: 0, assist: 0, foul: 0 },

  //   'Player 5': { goal: 0, assist: 0, foul: 0 },

  //   'Player 6': { goal: 0, assist: 0, foul: 0 },

  //   'Player 7': { goal: 0, assist: 0, foul: 0 },

  // });

  const [players, setPlayers] = useState<PlayerData>({
    Player1: { actions: [], actionCounts: { goal: 0, assist: 0, foul: 0 } },
    Player2: { actions: [], actionCounts: { goal: 0, assist: 0, foul: 0 } },
  });

  const openModal = (player: string) => {

    setSelectedPlayer(player);

  };



  const closeModal = () => {

    setSelectedPlayer(null);

  };


  // Handle recording an action from the modal
  const handleRecordAction = (player: string, actionType: keyof PlayerActionCounts) => {
    const actionTime = formatTime(elapsedTime);

    setPlayers((prevPlayers) => ({
      ...prevPlayers,
      [player]: {
        ...prevPlayers[player],
        actions: [...prevPlayers[player].actions, { type: actionType, time: actionTime }],
        actionCounts: {
          ...prevPlayers[player].actionCounts,
          [actionType]: prevPlayers[player].actionCounts[actionType] + 1,
        },
      },
    }));
    setSelectedPlayer(null); // Close the modal after recording
  };

  // const recordAction = (actionType: keyof PlayerActions) => {

  //   if (selectedPlayer) {

  //     setPlayerActions((prevActions) => ({

  //       ...prevActions,

  //       [selectedPlayer]: {

  //         ...prevActions[selectedPlayer],

  //         [actionType]: prevActions[selectedPlayer][actionType] + 1,

  //       },

  //     }));

  //   }

  // };

// Timer states
const [isRunning, setIsRunning] = useState(false);
const [elapsedTime, setElapsedTime] = useState(0); // Time in seconds

// Start/Stop the timer
useEffect(() => {
    if (isRunning) {
      const timer = setInterval(() => {
        setElapsedTime((prevTime) => prevTime + 1);
      }, 1000); // Increment every second

      // Clear the interval when isRunning changes or on component unmount
      return () => clearInterval(timer);
    }
  }, [isRunning]);

// Format elapsed time as MM:SS
const formatTime = (seconds: number) => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
};

// Toggle timer on Start/Stop button click
const handleStartStop = () => {
  setIsRunning(!isRunning);
};

  return (

    <div>

      <h1>Soccer Match Tracker</h1>

      {/* Match Clock */}
      <div style={{ fontSize: '2rem', marginBottom: '20px' }}>
        Match Time: {formatTime(elapsedTime)}
      </div>
      <button onClick={handleStartStop} style={{ padding: '10px 20px', fontSize: '1rem' }}>
        {isRunning ? 'Stop' : 'Start'}
      </button>

      {/* <Field onPlayerClick={openModal} playerActions={playerActions} /> */}

      {/* <PlayerModal

        player={selectedPlayer}

        onClose={closeModal}

        onRecordAction={handleRecordAction}

      /> */}

      {/* Display players and action counts */}
      <div style={{ marginTop: '20px' }}>
        {Object.keys(players).map((player) => (
          <div key={player} style={{ marginBottom: '10px' }}>
            <h3 onClick={() => setSelectedPlayer(player)} style={{ cursor: 'pointer' }}>{player}</h3>
            <p>Goals: {players[player].actionCounts.goal}</p>
            <p>Assists: {players[player].actionCounts.assist}</p>
            <p>Fouls: {players[player].actionCounts.foul}</p>
          </div>
        ))}
      </div>

      {/* Player Modal */}
      {selectedPlayer && (
        <PlayerModal
          player={selectedPlayer}
          onClose={() => setSelectedPlayer(null)}
          onRecordAction={(actionType) => handleRecordAction(selectedPlayer, actionType)}
        />
      )}

    </div>

  );

};



export default App;
