import React from 'react';

import PlayerPosition from './PlayerPosition';

import './Field.css';



interface FieldProps {

  onPlayerClick: (player: string) => void;

  playerActions: { [key: string]: { goal: number; assist: number; foul: number } };

}



const Field: React.FC<FieldProps> = ({ onPlayerClick, playerActions }) => {

  const players = [

    { id: '1', name: 'Player 1', top: '20%', left: '40%' },

    { id: '2', name: 'Player 2', top: '40%', left: '30%' },

    { id: '3', name: 'Player 3', top: '40%', left: '50%' },

    { id: '4', name: 'Goalkeeper', top: '80%', left: '45%' },

    { id: '5', name: 'Goalkeeper 1', top: '85%', left: '55%' },

  ];



  return (

    <div className="field">

      {players.map((player) => (

        <PlayerPosition

          key={player.id}

          player={player}

          onClick={() => onPlayerClick(player.name)}

          actions={playerActions[player.name]}

        />

      ))}

    </div>

  );

};



export default Field;
