import React, { useState } from 'react';

import Field from './components/Field';

import PlayerModal from './components/PlayerModal';



export interface PlayerActions {

  goal: number;

  assist: number;

  foul: number;

}



const App: React.FC = () => {

  const [selectedPlayer, setSelectedPlayer] = useState<string | null>(null);

  const [playerActions, setPlayerActions] = useState<{ [key: string]: PlayerActions }>({

    'Player 1': { goal: 0, assist: 0, foul: 0 },

    'Player 2': { goal: 0, assist: 0, foul: 0 },

    'Player 3': { goal: 0, assist: 0, foul: 0 },

    'Goalkeeper': { goal: 0, assist: 0, foul: 0 },

  });



  const openModal = (player: string) => {

    setSelectedPlayer(player);

  };



  const closeModal = () => {

    setSelectedPlayer(null);

  };



  const recordAction = (actionType: keyof PlayerActions) => {

    if (selectedPlayer) {

      setPlayerActions((prevActions) => ({

        ...prevActions,

        [selectedPlayer]: {

          ...prevActions[selectedPlayer],

          [actionType]: prevActions[selectedPlayer][actionType] + 1,

        },

      }));

    }

  };



  return (

    <div>

      <h1>Soccer Match Tracker</h1>

      <Field onPlayerClick={openModal} playerActions={playerActions} />

      <PlayerModal

        player={selectedPlayer}

        onClose={closeModal}

        onRecordAction={recordAction}

      />

    </div>

  );

};



export default App;
