import React, { useState, useEffect } from 'react';

interface PlayerAction {
  type: keyof PlayerActions;
  time: string; // Timestamp of the action in MM:SS format
}

interface PlayerActions {
  goal: number;
  assist: number;
  foul: number;
}

interface PlayerData {
  [playerName: string]: {
    actions: PlayerAction[]; // Store a list of actions with timestamps
  };
}

const App: React.FC = () => {
  const [players, setPlayers] = useState<PlayerData>({
    Player1: { actions: [] },
    Player2: { actions: [] },
    // Add other players as needed
  });

  // Timer states
  const [isRunning, setIsRunning] = useState(false);
  const [elapsedTime, setElapsedTime] = useState(0); // Time in seconds

  // Start/Stop the timer
  useEffect(() => {
    if (isRunning) {
      const timer = setInterval(() => {
        setElapsedTime((prevTime) => prevTime + 1);
      }, 1000); // Increment every second

      // Clear the interval when isRunning changes or on component unmount
      return () => clearInterval(timer);
    }
  }, [isRunning]);

  // Format elapsed time as MM:SS
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(mins).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
  };

  // Toggle timer on Start/Stop button click
  const handleStartStop = () => {
    setIsRunning(!isRunning);
  };

  // Record an action for a player with a timestamp
  const handleRecordAction = (player: string, actionType: keyof PlayerActions) => {
    const actionTime = formatTime(elapsedTime); // Get the formatted time

    setPlayers((prevPlayers) => ({
      ...prevPlayers,
      [player]: {
        ...prevPlayers[player],
        actions: [
          ...prevPlayers[player].actions,
          { type: actionType, time: actionTime }, // Add action with timestamp
        ],
      },
    }));
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Soccer Match Tracker</h1>

      {/* Match Clock */}
      <div style={{ fontSize: '2rem', marginBottom: '20px' }}>
        Match Time: {formatTime(elapsedTime)}
      </div>
      <button onClick={handleStartStop} style={{ padding: '10px 20px', fontSize: '1rem' }}>
        {isRunning ? 'Stop' : 'Start'}
      </button>

      {/* Render player actions with timestamps */}
      <div style={{ marginTop: '20px' }}>
        {Object.keys(players).map((player) => (
          <div key={player} style={{ marginBottom: '20px' }}>
            <h3>{player}</h3>
            <button onClick={() => handleRecordAction(player, 'goal')}>Record Goal</button>
            <button onClick={() => handleRecordAction(player, 'assist')}>Record Assist</button>
            <button onClick={() => handleRecordAction(player, 'foul')}>Record Foul</button>

            <h4>Actions:</h4>
            <ul>
              {players[player].actions.map((action, index) => (
                <li key={index}>
                  {action.type} at {action.time}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
